import React, {useEffect, useState} from 'react';
import Product from "../product/Product";
import styles from './Body.module.css';
import ProductDetails from "../productDetails/ProductDetails";

const Body = (props) => {

    const [productCurrent, setProductCurrent] = useState({...props.productCurrent});

    useEffect(()=>{
        setProductCurrent({...props.productCurrent})
    },[])
    return <React.Fragment>
        <div className={styles.container}>
            <div className={styles.body_wrapper}>
                <div className={styles.body_left}>
                    {props.products.map(product => {
                        return <div key={product.id}>
                            <Product showProductDetails={props.showProductDetails} product={product}/>
                        </div>
                    })}
                </div>
                <div className={styles.body_right}>
                    <ProductDetails productCurrent={productCurrent} updateProductDetails={props.updateProductDetails}/>
                </div>

            </div>
        </div>
    </React.Fragment>
}

export default Body;
