import React, {useState} from "react";
import styles from './ProductDetails.module.css';

const ProductDetails = (props) => {
    const [product, setProduct] = useState({...props.product});

    const updateProduct = (event) => {
        event.preventDefault();
        let name = event.target.name;
        let value = event.target.value;
        setProduct({...product, [name]: value})

    }

    return <React.Fragment>
        <div className={styles.details_wrapper}>
            <form>
                <span className={styles.wrapper_title}>
                {product.name + " Details"}
            </span>
                <div className={styles.details_item}>
                    <img src={product.image}/>
                </div>
                <div className={styles.details_item}>
                    <label>Name</label>
                    <input  type="text" name="name" onChange={updateProduct} value={product.name}/>
                </div>
                <div className={styles.details_item}>
                    <label>Description</label>
                    <textarea className={styles.details_item_input} type="text" name="description" onChange={updateProduct} value={product.description}/>
                </div>
            </form>
        </div>

    </React.Fragment>
}

export default ProductDetails;
