import React, {useState} from 'react';
import Header from "../header/Header";
import styles from "./Shop.module.css";
import Filter from "../filter/Filter";
import ProductList from "../productList/ProductList";
import ProductDetails from "../productDetails/ProductDetails";
import {Route} from "react-router-dom";
import Pagination from "../pagination/Pagination";

const Shop = (props) => {

    const [currentPage, setCurrentPage] = useState(1);
    const productsPerPag = useState(4);
    const lastProductsIndex = currentPage * productsPerPag;
    const firstProductsIndex = lastProductsIndex - productsPerPag;
    const currentProduct = props.products.slice(firstProductsIndex, lastProductsIndex);

    return <React.Fragment>
        <div className={styles.container}>
            <Header/>
            <Filter />
            <div className={styles.body_wrapper}>
                <div className={styles.body_left}>
                    <ProductList products={props.products}/>
                    <Pagination productsPerPag={productsPerPag} totalProducts={props.products.length}/>
                </div>
                <div className={styles.body_right}>
                    <Route path={'/shop/products/product/:'+ props.productCurrent.id}  render={() => {
                        return <ProductDetails productCurrent={props.productCurrent}/>
                    }}>
                    </Route>
                </div>
            </div>
        </div>
    </React.Fragment>
}

export default Shop;
