import React from 'react';
import Product from "../product/Product";
import styles from './Body.module.css';
import ProductDetails from "../productDetails/ProductDetails";
import {useSelector} from "react-redux";
import {NavLink} from "react-router-dom";
import {PATH_PRODUCT} from "../../config/confi_nav";
import {updateProductCurrent} from "../../actions/updateProductCurrent";


const Body = (props) => {

    const productCurrent = useSelector(state => state.products.productCurrent);
    const deleteProduct = (id) => {
          alert("delete " + id)
        }

        const getProductDetails = (product) => {
            store.dispatch(updateProductCurrent(product))
        }

    return <React.Fragment>
        <div className={styles.container}>
            <div className={styles.body_wrapper}>
                <div className={styles.body_left}>
                    {props.products.map(product => {
                        return <NavLink to={PATH_PRODUCT}  key={product.id} >
                            <div onClick={() => getProductDetails(props.product)} className={styles.product_wrapper}>
                                        <img className={styles.product_image} src={props.product.image} alt=" "/>
                                        <div className={styles.product_description}>
                                            <h3>{props.product.name}</h3>
                                            <p>{props.product.description}</p>
                                        </div>
                                        <div className={styles.product_info}>
                                            <div className={styles.product_info_wrapper}>
                                                <h4>Price:</h4>
                                                <div className={styles.product_info_content}>
                                                    <BsCurrencyDollar className={styles.info_icon}/>
                                                    <span>{props.product.price}</span>
                                                </div>

                                            </div>
                                        </div>
                                        <button className={styles.product_btn_delete} onClick={() => deleteProduct(props.product.id)}>Delete</button>
                                    </div>
                        </NavLink>
                    })}
                </div>
                <div className={styles.body_right}>
                    <ProductDetails productCurrent={productCurrent}/>
                </div>

            </div>
        </div>
    </React.Fragment>
}

export default Body;
