import Header from "../header/Header";
import styles from "./Shop.module.css";
import Body from "../body/Body";

const Shop = (props) => {


    const[products, setProducts] = useState([...state])
    const [productCurrent, setProductCurrent] = useState({...products[0]})

    const showProductDetails = (product) => {
        setProductCurrent({...product});
    }

    const updateProductDetails = (product) => {

        const newState = products.map(productState => {
            return productState.id === products.id ? product : productState;
        })
        // setProducts([...newState]);
        console.log(newState)


    }

    return <React.Fragment>
        <div className={styles.container}>
            <Header/>
            <Body productCurrent={productCurrent}
                  showProductDetails={showProductDetails}
                  updateProductDetails={updateProductDetails}
                  products={products}
            />
        </div>

    </React.Fragment>
}

export default Shop;
