import React from 'react';
import styles from './Filter.module.css'
import {BiSearch} from "react-icons/all";
import {updateProductsList} from "../../actions/udateProductsListBySearch";
import store from "../../store/store";

const Filter = (props) => {

    const onChange = (event) => {
        event.preventDefault();
        let name = event.target.name;
        let value = event.target.value;
        switch (name) {
            case 'search':
                store.dispatch(updateProductsList(value));
                break;
            case'sort':




        }
    }





    return <React.Fragment>
        <div className={styles.filter_wrapper}>
            <button className={styles.filter_btn_add}>Add product</button>

            <div className={styles.filter_item}>
                <BiSearch className={styles.filter_item_icon}/>
                <input onChange={onChange} type="text" name="search" placeholder="Search products..."/>
            </div>
            <div className={styles.filter_item}>
                <label htmlFor="sort">Sort by:</label>
                <select name="sort" onChange={onChange} id="sort">
                    <option selected value="name">Name</option>
                    <option value="id">Product ID</option>
                </select>
            </div>
        </div>


    </React.Fragment>
}

export default Filter;
