import React, {useState} from 'react';
import Product from "../product/Product";
import styles from './Body.module.css';
import ProductDetails from "../productDetails/ProductDetails";

const Body = (props) => {
    const products = [
        {id: 1,
            name: "Apple",
            description: "An apple is an edible fruit produced by an apple tree (Malus domestica). ",
            price: "3.50",
            creationDate: "13-09-2021",
            image: "https://image.freepik.com/free-photo/red-apple-with-green-leaf-white-background_1232-3290.jpg?2"
        },
        {id: 2,
            name: "Orange",
            description: "The orange is the fruit of various citrus species in the family Rutaceae. ",
            price: "4.50",
            creationDate: "22-03-2021",
            image: "https://image.freepik.com/free-vector/watercolor-orange-background_52683-10330.jpg"
        },

    ]
    const [productsState, setProductsState] = useState({...products});
    const [productCurrent, setProductCurrent] = useState({...productsState[0]});
    const showProductDetails = (product) => {
        setProductCurrent({...product});
    }

    const updateProductDetails = (product) => {
        // let productFind = productsState.find(productState => {
        //     return productState.id === product.id;
        // })
        console.log(product)

    }
    return <React.Fragment>
        <div className={styles.container}>
            <div className={styles.body_wrapper}>
                <div className={styles.body_left}>
                    {products.map(product => {
                        return <div key={product.id}>
                            <Product showProductDetails={showProductDetails} product={product}/>
                        </div>
                    })}
                </div>
                <div className={styles.body_right}>
                    <ProductDetails product={productCurrent} updateProductDetails={updateProductDetails}/>
                </div>

            </div>
        </div>
    </React.Fragment>
}

export default Body;
