import React, {useEffect, useState} from 'react';
import Product from "../product/Product";
import styles from './Body.module.css';
import ProductDetails from "../productDetails/ProductDetails";
import {useSelector} from "react-redux";
import store from "../../store/store";
import {NavLink} from "react-router-dom";
import {PATH_PRODUCT} from "../../config/confi_nav";

const Body = (props) => {

    const productCurrent = useSelector(state => state.products.productCurrent);

    return <React.Fragment>
        <div className={styles.container}>
            <div className={styles.body_wrapper}>
                <div className={styles.body_left}>
                    {props.products.map(product => {
                        return <NavLink key={product.id} to={PATH_PRODUCT + '/' + product.id}>
                                <Product product={product}/>
                            </NavLink>

                    })}
                </div>
                <div className={styles.body_right}>
                    <ProductDetails productCurrent={productCurrent}/>
                </div>

            </div>
        </div>
    </React.Fragment>
}

export default Body;
