import React, {useEffect, useState} from 'react';
import {PATH_SHOP} from "./config/confi_nav";
import Shop from "./components/shop/Shop";
import {Redirect, Route, Switch} from "react-router-dom";
import './App.css';
import {useSelector} from "react-redux";
import ProductDetails from "./components/productDetails/ProductDetails";

function App() {

    const state = useSelector(state => state.products);
    const [products, setProducts] = useState([...state.products]);
    const productCurrent = useSelector(state => state.products.productCurrent);

    useEffect(()=>{
        setProducts([...state.products])
    },[state])

  return <React.Fragment>
      {/*<Redirect to={PATH_SHOP}/>*/}
      <Switch>
          <Route path={PATH_SHOP} exact render={() => {
              return <Shop products={products}/>
          }}>
          </Route>
          <Route path={'/products/product:' + productCurrent.id} exact render={() => {
              return <ProductDetails products={products} productCurrent={productCurrent}/>
          }}>
          </Route>
        </Switch>
  </React.Fragment>
}

export default App;
