    }
]

const initialState = {
    products: products,
    productCurrent: products[0],
    sortBy: ""
}

export function productsReducer (state = initialState, action){

    switch (action.type){
        case 'UPDATE_PRODUCT_DETAILS':
            const tempProductsList = state.products.map(product => {
                return product.id === action.payload.id ? action.payload : product;
            })

            return {...state, products: tempProductsList};

        case 'UPDATE_PRODUCTS_LIST_BY_SEARCH':
            if(action.payload ===""){
                return {...state, products: products};
            }
            const productsListBySearch = state.products.filter(product => {
                return product.name.toLowerCase().includes(action.payload.toLowerCase()) === true ? product : ""
            })
            return {...state, products: productsListBySearch};

        case 'UPDATE_PRODUCTS_LIST_BY_SORT':
            let productsListBySort = [];
            switch (action.payload){
                case 'name':
                    productsListBySort = state.products.sort((a,b) => {
                        return a.name > b.name ? 1 : -1;
                    })
                    return {...state, products: productsListBySort, sortBy: action.payload};
                case 'id':
                    productsListBySort = state.products.sort((a,b) => {
                        return a.id > b.id ? 1 : -1;
                    })
                    return {...state, products: productsListBySort, sortBy: action.payload};
                case 'price':
                    productsListBySort = state.products.sort((a,b) => {
                        return Number.parseFloat(a.price) > Number.parseFloat(b.price) ? 1 : -1;
                    })
                    return {...state, products: productsListBySort, sortBy: action.payload};
                default:
                    return {...state, products: products, sortBy: action.payload};
            }

        case 'UPDATE_PRODUCT_CURRENT':
            return {...state, productCurrent: action.payload}

        case 'ADD_NEW_PRODUCT':
            const newProducts = [...state.products.push({
                id: +state.products.length + 1,
                name: "",
                description: "",
                price: 0,
                creationDate: new Date().toLocaleDateString(),
                image: 'https://image.flaticon.com/icons/png/512/266/266387.png'
            })];
            return {...state, products: [...newProducts]}



        default:
            return state;
    }
}
