import React from 'react';
import styles from './Product.module.css'
import {BsCurrencyDollar} from "react-icons/all";
import store from "../../store/store";
import {updateProductCurrent} from "../../actions/updateProductCurrent";
import {deleteProduct} from "../../actions/deleteProduct";

const Product =(props)=> {

    const getProductDetails = (product) => {
        store.dispatch(updateProductCurrent(product))
    }

    return <React.Fragment>
        <div onClick={() => getProductDetails(props.product)} className={styles.product_wrapper}>
            <img className={styles.product_image} src={props.product.image} alt=" "/>
            <div className={styles.product_description}>
                <h3>{props.product.name}</h3>
                <p>{props.product.description}</p>
            </div>
            <div className={styles.product_info}>
                <div className={styles.product_info_wrapper}>
                    <h4>Price:</h4>
                    <div className={styles.product_info_content}>
                        <BsCurrencyDollar className={styles.info_icon}/>
                        <span>{props.product.price}</span>
                    </div>
                </div>
            </div>
            <button className={styles.product_btn_delete} onClick={() => store.dispatch(deleteProduct(props.product.id))}>Delete</button>
        </div>

    </React.Fragment>
}
export default Product;
