import React from 'react';
import styles from './Filter.module.css'
import {BiSearch} from "react-icons/all";
import {updateProductsList} from "../../actions/udateProductsList";
import store from "../../store/store";

const Filter = (props) => {

    const onChangeName = (event) => {
        event.preventDefault();
        store.dispatch(updateProductsList(event.target.value))

    }

    return <React.Fragment>
        <div className={styles.filter_wrapper}>
            <button className={styles.filter_btn_add}>Add product</button>

            <div className={styles.filter_item}>
                <BiSearch className={styles.filter_item_icon}/>
                <input onChange={onChangeName} type="text" name="search" placeholder="Search products..."/>
            </div>
            <div className={styles.filter_item}>
                <label htmlFor="sort">Sort by:</label>
                <select id="sort">
                    <option> selected>Name</option>
                    <option>Product ID</option>
                </select>
            </div>
        </div>


    </React.Fragment>
}

export default Filter;
