import React, {useEffect} from 'react';
import Product from "../product/Product";
import styles from './Body.module.css';
import ProductDetails from "../productDetails/ProductDetails";
import {useSelector} from "react-redux";

const Body = (props) => {

    const [products, setProducts] = useSelector(state => state.products.products);
    const productCurrent = useSelector(state => state.products.productCurrent);

    useEffect(()=> {
        setProducts(...useSelector(state => state.products))
    },[props])

    return <React.Fragment>
        <div className={styles.container}>
            <div className={styles.body_wrapper}>
                <div className={styles.body_left}>
                    {products.map(product => {
                        return <div key={product.id}>
                            <Product product={product}/>
                        </div>
                    })}
                </div>
                <div className={styles.body_right}>
                    <ProductDetails productCurrent={productCurrent}/>
                </div>

            </div>
        </div>
    </React.Fragment>
}

export default Body;
